package com.ll.exam.app10.API;import jakarta.persistence.Table;import lombok.AllArgsConstructor;import lombok.Data;import lombok.NoArgsConstructor;@Data@NoArgsConstructor@AllArgsConstructor@Table(name = "Resource")class Mp3File {    private long id;    private String name;    private byte[] content;    public Mp3File(String name, byte[] content) {        this.name = name;        this.content = content;    }}