package com.ll.exam.app10.API;import jakarta.persistence.Table;import lombok.AllArgsConstructor;import lombok.Data;@Data@AllArgsConstructor@Table(name = "song")public class Song {    private long id;    private String name;    private String artist;    private String album;    private String length;    private String resourceId;    private int year;    public Song(String name, String artist, String album, String length, String resourceId, int year) {        this.name = name;        this.artist = artist;        this.album = album;        this.length = length;        this.resourceId = resourceId;        this.year = year;    }}